import time, osfrom os.path import join, expanduser, existsfrom jca.web_stuff.web_get import downloaddef download_files(lines):  dir_path = join(expanduser('~'), 'Desktop', 'downloads')  if not exists(dir_path):  os.mkdir(dir_path)  for i, line in enumerate(lines):    download(line, join(dir_path, line.rsplit('/', 1)[-1]))    time.sleep(1)  return linesif __name__ == '__main__':  download_files(['http://www.google.com/intl/en_ALL/images/logo.gif'])